import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import "../css/Encuesta.css";

const Encuesta = () => {
  const { token } = useParams();
  const [valido, setValido] = useState(null);
  const [respuestas, setRespuestas] = useState({
    respuesta1: 0,
    respuesta2: 0,
  });
  const [enviado, setEnviado] = useState(false);

  useEffect(() => {
    fetch(`http://localhost:3000/api/encuestas/validar/${token}`)
      .then((res) => {
        if (!res.ok) throw new Error("Link inválido");
        setValido(true);
      })
      .catch(() => setValido(false));
  }, [token]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await fetch(
        `http://localhost:3000/api/encuestas/responder/${token}`,
        {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify(respuestas),
        }
      );

      if (!res.ok) throw new Error("Error al enviar respuestas");
      setEnviado(true);
    } catch (err) {
      console.error("Error:", err.message);
    }
  };

  const renderEstrellas = (preguntaKey) => (
    <div className="estrellas-container">
      <div className="estrellas-linea">
        {[1, 2, 3, 4, 5].map((num) => (
          <span
            key={num}
            onClick={() =>
              setRespuestas((prev) => ({ ...prev, [preguntaKey]: num }))
            }
            className={
              respuestas[preguntaKey] >= num ? "estrella activa" : "estrella"
            }
            role="button"
          >
            ★
          </span>
        ))}
      </div>
      <div className="leyendas">
        <span>Pésimo</span>
        <span>Excelente</span>
      </div>
    </div>
  );

  if (valido === false) return <p>Este link ya fue usado o es inválido.</p>;
  if (enviado) return <p>¡Gracias por completar la encuesta!</p>;

  return (
    <form onSubmit={handleSubmit}>
      <h2>Encuesta de Satisfacción</h2>

      <div>
        <label>¿Qué te pareció el servicio?</label>
        {renderEstrellas("respuesta1")}
      </div>

      <div>
        <label>¿Cómo podríamos mejorar?</label>
        {renderEstrellas("respuesta2")}
      </div>

      <button
        type="submit"
        disabled={!respuestas.respuesta1 || !respuestas.respuesta2}
      >
        Enviar
      </button>
    </form>
  );
};

export default Encuesta;
