import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import "../css/Encuesta.css";

const Encuesta = () => {
  const { token } = useParams();
  const [valido, setValido] = useState(null);
  const [preguntas, setPreguntas] = useState([]);
  const [respuestas, setRespuestas] = useState({});
  const [enviado, setEnviado] = useState(false);

  useEffect(() => {
    fetch(`http://localhost:3000/api/encuestas/validar/${token}`)
      .then((res) => {
        if (!res.ok) throw new Error("Link inválido");
        return res.json();
      })
      .then(() => {
        setValido(true);

        return fetch("http://localhost:3000/api/encuestas/preguntas");
      })
      .then((res) => res.json())
      .then((data) => {
        setPreguntas(data.preguntas);

        const respuestasIniciales = {};
        data.preguntas.forEach((p) => {
          respuestasIniciales[p.id] = 0;
        });
        setRespuestas(respuestasIniciales);
      })
      .catch(() => setValido(false));
  }, [token]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await fetch(
        `http://localhost:3000/api/encuestas/responder/${token}`,
        {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify(respuestas),
        }
      );
      if (!res.ok) throw new Error("Error al enviar respuestas");
      setEnviado(true);
    } catch (err) {
      console.error("Error:", err.message);
    }
  };

  const renderEstrellas = (id) => (
    <div className="estrellas-container">
      <div className="estrellas-linea">
        {[1, 2, 3, 4, 5].map((num) => (
          <span
            key={num}
            onClick={() => setRespuestas({ ...respuestas, [id]: num })}
            className={respuestas[id] >= num ? "estrella activa" : "estrella"}
            role="button"
          >
            ★
          </span>
        ))}
      </div>
      <div className="leyendas">
        <span>Pésimo</span>
        <span>Excelente</span>
      </div>
    </div>
  );

  if (valido === false)
    return (
      <div className="pantalla">
        <div className="formulario-wrapper">
          <p>Este link ya fue usado o es inválido.</p>
        </div>
      </div>
    );

  if (enviado)
    return (
      <div className="pantalla">
        <div className="formulario-wrapper">
          <p>¡Gracias por completar la encuesta!</p>
        </div>
      </div>
    );

  return (
    <div className="pantalla">
      <div className="formulario-wrapper">
        <h2 className="titulo">Encuesta de Satisfacción</h2>
        <form className="formulario" onSubmit={handleSubmit}>
          {preguntas.map((pregunta) => (
            <div className="form-grupo" key={pregunta.pregunta_id}>
              <label>{pregunta.pregunta}</label>
              {renderEstrellas(pregunta.pregunta_id)}
            </div>
          ))}

          <div className="botones">
            <button
              type="submit"
              disabled={Object.values(respuestas).some((val) => val === 0)}
            >
              Enviar
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default Encuesta;
