import React, { useEffect, useState } from "react";
import axios from "axios";

const Encuesta = ({ token }) => {
  const [valido, setValido] = useState(null);
  const [respuestas, setRespuestas] = useState({
    respuesta1: "",
    respuesta2: "",
  });
  const [enviado, setEnviado] = useState(false);

  useEffect(() => {
    axios
      .get(`http://localhost:5000/api/validar/${token}`)
      .then(() => setValido(true))
      .catch(() => setValido(false));
  }, [token]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    await axios.post(
      `http://localhost:5000/api/responder/${token}`,
      respuestas
    );
    setEnviado(true);
  };

  if (valido === false) return <p>Este link ya fue usado o es inválido.</p>;
  if (enviado) return <p>¡Gracias por completar la encuesta!</p>;

  return (
    <form onSubmit={handleSubmit}>
      <h2>Encuesta de Satisfacción</h2>
      <input
        placeholder="¿Qué te pareció el servicio?"
        value={respuestas.respuesta1}
        onChange={(e) =>
          setRespuestas({ ...respuestas, respuesta1: e.target.value })
        }
      />
      <input
        placeholder="¿Cómo podríamos mejorar?"
        value={respuestas.respuesta2}
        onChange={(e) =>
          setRespuestas({ ...respuestas, respuesta2: e.target.value })
        }
      />
      <button type="submit">Enviar</button>
    </form>
  );
};

export default Encuesta;
