import React, { useEffect, useState } from "react";
import "./css/PanelMedico.css";

const PanelMedico = ({ user, onLogout }) => {
  const [turnos, setTurnos] = useState([]);
  const [historiasClinicas, setHistoriasClinicas] = useState({});
  const MEDICO_ID = user.id;
  const MEDICO_NOMBRE = user.nombre;

  useEffect(() => {
    const obtenerDatos = async () => {
      try {
        const resTurnos = await fetch(
          `${process.env.REACT_APP_API_TURNOS_OCUPADOS_HOY}/${MEDICO_ID}`
        );
        if (!resTurnos.ok) throw new Error("Error al obtener turnos");
        const turnosData = await resTurnos.json();
        setTurnos(turnosData);
      } catch (error) {
        console.error("Error al cargar datos:", error);
      }
    };

    obtenerDatos();
  }, [MEDICO_ID]);

  const cambiarEstado = async (turnoId, nuevoEstadoId) => {
    try {
      const res = await fetch(
        `${process.env.REACT_APP_API_MODIFICAR_ESTADO_TURNO}/${turnoId}/estado`,
        {
          method: "PUT",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify({ estado_id: nuevoEstadoId }),
        }
      );

      if (!res.ok) throw new Error("No se pudo actualizar el estado");

      setTurnos((prev) =>
        prev.map((t) =>
          t.turno_id === turnoId
            ? {
                ...t,
                estado_id: nuevoEstadoId,
                estado: nuevoEstadoId === "A" ? "Atendido" : "Ausente",
              }
            : t
        )
      );
    } catch (err) {
      console.error("Error al cambiar estado:", err.message);
    }
  };
  const verHistoriaClinica = async (id_externo_paciente, turnoId) => {
    if (historiasClinicas.hasOwnProperty(turnoId)) {
      setHistoriasClinicas((prev) => {
        const nuevo = { ...prev };
        delete nuevo[turnoId];
        return nuevo;
      });
      return;
    }
    try {
      const res = await fetch(process.env.REACT_APP_API_HISTORIAS, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ id_externo: id_externo_paciente }),
      });

      const data = await res.json();
      if (!res.ok) throw new Error(data.error);

      setHistoriasClinicas((prev) => ({
        ...prev,
        [turnoId]: data.historia,
      }));
    } catch (error) {
      setHistoriasClinicas((prev) => ({
        ...prev,
        [turnoId]: null,
      }));
      console.error("Error al obtener historia clÃ­nica:", error.message);
    }
  };

  if (!turnos) {
    return <div>Cargando turnos...</div>;
  }
  return (
    <div
      style={{
        display: "flex",
        flexDirection: "column",
        height: "100vh",
      }}
    >
      {/* ----------------------------------------- Header --------------------------------------------*/}
      <header
        style={{
          display: "flex",
          alignItems: "center",
          padding: "10px 20px",
          backgroundColor: "#007bff",
          color: "white",
          gap: "10px",
        }}
      >
        {headerIconUrl && (
          <img
            src={headerIconUrl}
            alt="Icono"
            style={{ height: 40, width: 110, objectFit: "contain" }}
          />
        )}
        <h1 style={{ margin: 0, fontSize: "1.5rem" }}>{headerTitle}</h1>
      </header>

      {/* -------------------------------------------------------------- Body ------------------------------------------------------ */}
      <div style={{ display: "flex", flexGrow: 1 }}>
        {/* ---------------------------------------- MenÃº lateral --------------------------------------------------------- */}
        <nav
          style={{
            width: 200,
            borderRight: "1px solid #ddd",
            padding: "20px",
            backgroundColor: "#f8f9fa",
            display: "flex",
            flexDirection: "column",
            gap: "10px",
          }}
        >
          <button
            onClick={onLogout}
            style={{
              padding: "10px",
              cursor: "pointer",
              backgroundColor: "transparent",
              color: "black",
              border: "none",
              borderRadius: 4,
              textAlign: "left",
              marginTop: "auto",
            }}
          >
            Salir
          </button>
        </nav>

        {/* -------------------------------------- Contenido principal ----------------------------------------- */}
        <main style={{ flexGrow: 1, padding: 20 }}>
          {vista === null && (
            <div>
              <h2>Bienvenido, {MEDICO_NOMBRE}!</h2>
              <p>Seleccione una opciÃ³n del menÃº para comenzar.</p>
            </div>
          )}

          {vista === "reserva" && (
            <div>
              <h1>Especialidades mÃ©dicas</h1>

              {loadingEspecialidades && <p>Cargando especialidades...</p>}

              {especialidades.length > 0 && (
                <div style={{ marginTop: "20px" }}>
                  <label>Seleccionar especialidad:</label>
                  <select
                    value={especialidadSeleccionada}
                    onChange={handleChangeEspecialidad}
                    style={{
                      marginLeft: "10px",
                      padding: "5px",
                    }}
                  >
                    <option value="">Seleccione una</option>
                    {especialidades.map((esp) => (
                      <option
                        key={esp.especialidad_id}
                        value={esp.especialidad_id}
                      >
                        {esp.descripcion}
                      </option>
                    ))}
                  </select>
                  <div style={{ marginTop: "10px" }}>
                    <label>Fecha:</label>
                    <input
                      type="date"
                      value={fechaSeleccionada}
                      min={new Date().toISOString().split("T")[0]}
                      onChange={handleChangeFecha}
                      style={{
                        marginLeft: "10px",
                        padding: "5px",
                      }}
                    />
                  </div>
                </div>
              )}

              {loadingDisponibles && <p>Cargando turnos disponibles...</p>}
              {especialidadSeleccionada &&
                !loadingDisponibles &&
                disponibles.length === 0 && (
                  <p>No hay turnos disponibles en esa fecha</p>
                )}
              {disponibles.length > 0 && (
                <div style={{ marginTop: "20px" }}>
                  <h2>Turnos disponibles</h2>
                  <table border="1" cellPadding="8" cellSpacing="0">
                    <thead>
                      <tr>
                        <th>Fecha</th>
                        <th>MÃ©dico ID</th>
                        <th></th>
                      </tr>
                    </thead>
                    <tbody>
                      {disponibles.map((item, index) => (
                        <tr key={index}>
                          <td>{formatearFechaLocal(item.fecha)}</td>
                          <td>{item.medico_id}</td>
                          <td>
                            <button
                              onClick={() =>
                                abrirModalConfirmacion({
                                  turno_id: item.turno_id,
                                  fecha: formatearFechaLocal(item.fecha),
                                  medico_id: item.medico_id,
                                })
                              }
                            >
                              Reservar
                            </button>
                          </td>
                        </tr>
                      ))}
                    </tbody>
                  </table>
                </div>
              )}
            </div>
          )}

          {vista === "ocupados" && (
            <div>
              {loadingTurnosOcupados && <p>Cargando turnos ocupados...</p>}

              {ocupados.length > 0 && (
                <div style={{ marginTop: "20px" }}>
                  <h2>Turnos ocupados</h2>
                  <table border="1" cellPadding="8" cellSpacing="0">
                    <thead>
                      <tr>
                        <th>Fecha</th>
                        <th>MÃ©dico ID</th>
                        <th></th>
                      </tr>
                    </thead>
                    <tbody>
                      {ocupados.map((item, index) => (
                        <tr key={index}>
                          <td>{formatearFechaLocal(item.fecha)}</td>
                          <td>{item.medico_id}</td>
                          <td>
                            <button
                              onClick={() => {
                                setTurnoAModificar(item);
                                const fecha = new Date(item.fecha)
                                  .toISOString()
                                  .split("T")[0];
                                setFechaSeleccionadaParaModificacion(fecha);
                                cargarTurnosParaModificacion(
                                  item.especialidad_id,
                                  fecha
                                );
                              }}
                            >
                              Modificar
                            </button>
                            <button
                              onClick={() =>
                                abrirModalConfirmarCancelacion({
                                  turno_id: item.turno_id,
                                  fecha: formatearFechaLocal(item.fecha),
                                  medico_id: item.medico_id,
                                })
                              }
                            >
                              Cancelar
                            </button>
                          </td>
                        </tr>
                      ))}
                    </tbody>
                  </table>
                </div>
              )}

              {modificandoTurno && turnosNuevosParaModificacion.length >= 0 && (
                <div style={{ marginTop: "10px" }}>
                  <label>Fecha:</label>
                  <input
                    type="date"
                    value={fechaSeleccionadaParaModificacion}
                    onChange={handleChangeFechaModificacion}
                    min={new Date().toISOString().split("T")[0]}
                    style={{
                      marginLeft: "10px",
                      padding: "5px",
                    }}
                  />
                </div>
              )}
              {modificandoTurno &&
                turnosNuevosParaModificacion.length === 0 && (
                  <p>No hay turnos disponibles en esa fecha</p>
                )}
              {modificandoTurno && turnosNuevosParaModificacion.length > 0 && (
                <div style={{ marginTop: "20px" }}>
                  <h2>Turnos disponibles</h2>
                  <table border="1" cellPadding="8" cellSpacing="0">
                    <thead>
                      <tr>
                        <th>Fecha</th>
                        <th>MÃ©dico ID</th>
                        <th></th>
                      </tr>
                    </thead>
                    <tbody>
                      {turnosNuevosParaModificacion.map((item, index) => (
                        <tr key={index}>
                          <td>{formatearFechaLocal(item.fecha)}</td>
                          <td>{item.medico_id}</td>
                          <td>
                            <button
                              onClick={() =>
                                abrirModalConfirmarModificacion({
                                  turno_id: item.turno_id,
                                  fecha: formatearFechaLocal(item.fecha),
                                  medico_id: item.medico_id,
                                })
                              }
                            >
                              Reservar
                            </button>
                          </td>
                        </tr>
                      ))}
                    </tbody>
                  </table>
                </div>
              )}
            </div>
          )}
        </main>
      </div>
    </div>
  );
};

export default PanelMedico;
