import React, { useEffect, useState } from "react";
import "../css/PanelMedico.css";

const medicoId = 1; // podrías traerlo de login

const PanelMedico = () => {
  const [turnos, setTurnos] = useState([]);

  useEffect(() => {
    const fetchTurnos = async () => {
      const res = await fetch(
        `http://localhost:3000/api/turnos/hoy/${medicoId}`
      );
      const data = await res.json();
      setTurnos(data);
    };
    fetchTurnos();
  }, []);

  const cambiarEstado = async (turnoId, nuevoEstadoId) => {
    await fetch(`http://localhost:3000/api/turnos/${turnoId}/estado`, {
      method: "PUT",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ estado_id: nuevoEstadoId }),
    });

    setTurnos(
      turnos.map((t) =>
        t.turno_id === turnoId
          ? {
              ...t,
              estado_id: nuevoEstadoId,
              estado: nuevoEstadoId === "A" ? "Atendido" : "Ausente",
            }
          : t
      )
    );
  };

  return (
    <div className="pantalla">
      <h2>Turnos de hoy</h2>
      <ul className="lista-turnos">
        {turnos.map((turno) => (
          <li key={turno.turno_id} className="turno-item">
            <div>
              <strong>{turno.paciente_nombre}</strong> — {turno.hora} — Estado:{" "}
              <span className={`estado ${turno.estado.toLowerCase()}`}>
                {turno.estado}
              </span>
            </div>
            <div className="botones-turno">
              <button
                onClick={() => cambiarEstado(turno.turno_id, "A")}
                disabled={turno.estado_id !== "L"}
              >
                Atendido
              </button>
              <button
                onClick={() => cambiarEstado(turno.turno_id, "U")}
                disabled={turno.estado_id !== "L"}
              >
                Ausente
              </button>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default PanelMedico;
