import React, { useEffect, useState } from "react";
import axios from "axios";
import {
  Button,
  Card,
  CardContent,
  CardFooter,
  CardHeader,
  CardTitle,
} from "@/components/ui/card";
import { Calendar } from "@/components/ui/calendar";
import { Modal } from "@/components/ui/modal";
import { format } from "date-fns";

function App() {
  const [fechaSeleccionada, setFechaSeleccionada] = useState(new Date());
  const [turnos, setTurnos] = useState([]);
  const [modal, setModal] = useState(false);
  const [turnoSeleccionado, setTurnoSeleccionado] = useState(null);
  const [vista, setVista] = useState("inicio"); // 'inicio' | 'reserva' | 'ocupados'

  const cargarTurnos = async () => {
    const url = `${process.env.REACT_APP_API_TURNOS}?fecha=${
      fechaSeleccionada.toISOString().split("T")[0]
    }`;
    try {
      const respuesta = await axios.get(url);
      setTurnos(respuesta.data);
    } catch (error) {
      console.error("Error al cargar turnos:", error);
    }
  };

  const reservarTurno = async () => {
    if (!turnoSeleccionado) return;
    const url = `${process.env.REACT_APP_API_RESERVAR}/${turnoSeleccionado.id}`;
    try {
      await axios.put(url);
      setModal(false);
      cargarTurnos();
    } catch (error) {
      console.error("Error al reservar turno:", error);
    }
  };

  const cancelarTurno = async (turno) => {
    const url = `${process.env.REACT_APP_API_CANCELAR}/${turno.id}`;
    try {
      await axios.put(url);
      cargarTurnos();
    } catch (error) {
      console.error("Error al cancelar turno:", error);
    }
  };

  const modificarTurno = async (turno) => {
    const url = `${process.env.REACT_APP_API_MODIFICAR}/${turno.id}`;
    try {
      await axios.put(url);
      cargarTurnos();
    } catch (error) {
      console.error("Error al modificar turno:", error);
    }
  };

  useEffect(() => {
    if (vista === "reserva" || vista === "ocupados") {
      cargarTurnos();
    }
  }, [fechaSeleccionada, vista]);

  const renderContenido = () => {
    if (vista === "reserva") {
      return (
        <>
          <h2 className="text-xl font-semibold mb-4">Reservar nuevo turno</h2>
          <Calendar
            mode="single"
            selected={fechaSeleccionada}
            onSelect={setFechaSeleccionada}
            className="mb-4"
          />
          <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
            {turnos
              .filter((t) => !t.paciente)
              .map((turno) => (
                <Card key={turno.id}>
                  <CardHeader>
                    <CardTitle>{turno.hora}</CardTitle>
                  </CardHeader>
                  <CardContent>
                    <p>Especialidad: {turno.especialidad}</p>
                  </CardContent>
                  <CardFooter>
                    <Button
                      onClick={() => {
                        setTurnoSeleccionado(turno);
                        setModal(true);
                      }}
                    >
                      Reservar
                    </Button>
                  </CardFooter>
                </Card>
              ))}
          </div>
        </>
      );
    }

    if (vista === "ocupados") {
      return (
        <>
          <h2 className="text-xl font-semibold mb-4">Turnos ocupados</h2>
          <Calendar
            mode="single"
            selected={fechaSeleccionada}
            onSelect={setFechaSeleccionada}
            className="mb-4"
          />
          <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
            {turnos
              .filter((t) => t.paciente)
              .map((turno) => (
                <Card key={turno.id}>
                  <CardHeader>
                    <CardTitle>{turno.hora}</CardTitle>
                  </CardHeader>
                  <CardContent>
                    <p>Especialidad: {turno.especialidad}</p>
                    <p>Paciente: {turno.paciente}</p>
                  </CardContent>
                  <CardFooter className="flex gap-2">
                    <Button onClick={() => modificarTurno(turno)}>
                      Modificar
                    </Button>
                    <Button
                      variant="destructive"
                      onClick={() => cancelarTurno(turno)}
                    >
                      Cancelar
                    </Button>
                  </CardFooter>
                </Card>
              ))}
          </div>
        </>
      );
    }

    return (
      <div className="text-center text-lg">
        Bienvenido, {process.env.REACT_APP_NOMBRE_MEDICO}
      </div>
    );
  };

  return (
    <div className="min-h-screen flex flex-col">
      {/* Header */}
      <header className="bg-blue-600 text-white p-4 flex items-center">
        <img
          src={process.env.REACT_APP_ICONO_URL}
          alt="Logo"
          className="h-8 w-8 mr-4"
        />
        <h1 className="text-2xl font-bold">{process.env.REACT_APP_TITULO}</h1>
      </header>

      {/* Cuerpo */}
      <div className="flex flex-1">
        {/* Menú lateral */}
        <aside className="w-64 bg-gray-100 p-4">
          <nav className="flex flex-col gap-2">
            <Button variant="outline" onClick={() => setVista("reserva")}>
              Reservar nuevo turno
            </Button>
            <Button variant="outline" onClick={() => setVista("ocupados")}>
              Ver turnos ocupados
            </Button>
            <Button variant="destructive" onClick={() => setVista("inicio")}>
              Salir
            </Button>
          </nav>
        </aside>

        {/* Contenido principal */}
        <main className="flex-1 p-6 bg-white">{renderContenido()}</main>
      </div>

      {/* Modal */}
      <Modal isOpen={modal} onClose={() => setModal(false)}>
        <div className="p-6">
          <h2 className="text-xl mb-2">¿Confirmar reserva?</h2>
          <p>
            <strong>Fecha:</strong>{" "}
            {format(new Date(turnoSeleccionado?.fecha || ""), "dd/MM/yyyy")}
          </p>
          <p>
            <strong>Hora:</strong> {turnoSeleccionado?.hora}
          </p>
          <p>
            <strong>Especialidad:</strong> {turnoSeleccionado?.especialidad}
          </p>
          <div className="mt-4 flex justify-end gap-2">
            <Button onClick={reservarTurno}>Confirmar</Button>
            <Button variant="outline" onClick={() => setModal(false)}>
              Cancelar
            </Button>
          </div>
        </div>
      </Modal>
    </div>
  );
}

export default App;
