import React, { useState } from "react";
import axios from "axios";
import Main from "./Main";

function LoginForm() {
    const usuarioRol = Object.freeze({
        MEDICO: "MEDICO",
        PACIENTE: "PACIENTE",
        ADMINISTRATIVO: "ADMINISTRATIVO",
    });
    const [form, setForm] = useState({
        username: "",
        password: "",
        role: "Paciente",
    });
    const [loggedInUser, setLoggedInUser] = useState(null);
    const [error, setError] = useState("");

    const handleChange = (e) => {
        const { name, value } = e.target;
        setForm((prev) => ({ ...prev, [name]: value }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setError("");

        try {
            const tokenResponse = await axios.post(
                `${process.env.REACT_APP_API_URL}/token`,
                {
                    client_id: process.env.REACT_APP_CLIENT_ID,
                    client_secret: process.env.REACT_APP_CLIENT_SECRET,
                }
            );

            const token = tokenResponse.data.token;

            const rol = form.role
                .normalize("NFD")
                .replace(/[\u0300-\u036f]/g, "")
                .toUpperCase();
            const loginResponse = await axios.post(
                `${process.env.REACT_APP_API_URL}/login`,
                {
                    username: form.username,
                    password: form.password,
                    role: rol,
                },
                {
                    headers: {
                        Authorization: `Bearer ${token}`,
                    },
                }
            );

            console.log(
                "Login:" +
                    loginResponse.data.username +
                    " " +
                    loginResponse.data.role +
                    " " +
                    loginResponse.data.id
            );

            let api = "";

            if (rol === usuarioRol.PACIENTE) {
                console.log("Soy un paciente");
                api = `${process.env.REACT_APP_API_PACIENTE_POR_ID_EXTERNO}/${id_externo}`;
            } else if (rol === usuarioRol.MEDICO) {
                api = `${process.env.REACT_APP_API_MEDICO_POR_ID_EXTERNO}/${id_externo}`;
            }

            const userResponse = await axios.get(
                `${api}/${loginResponse.data.id}`
            );

            setLoggedInUser({
                username: loginResponse.data.username,
                role: loginResponse.data.role,
                id_externo: loginResponse.data.id,
                id: userResponse.data.id,
                nombre: userResponse.data.nombre,
            });
        } catch (err) {
            setError("Login fallido. Verifique las credenciales.");
        }
    };

    const handleLogout = () => {
        setLoggedInUser(null);
        setForm({ username: "", password: "", role: "Paciente" });
    };

    if (loggedInUser)
        return <Main user={loggedInUser} onLogout={handleLogout} />;

    return (
        <div className="container d-flex justify-content-center align-items-center vh-100">
            <div
                className="card p-4 shadow"
                style={{ maxWidth: "400px", width: "100%" }}
            >
                <div className="text-center mb-3">
                    <img
                        src="/logo.png"
                        alt="Logo"
                        style={{ maxWidth: "100px", marginBottom: "10px" }}
                    />
                    <h2 className="mb-3">Iniciar Sesión</h2>
                </div>
                <form onSubmit={handleSubmit}>
                    <div className="mb-3">
                        <label htmlFor="username" className="form-label">
                            Usuario
                        </label>
                        <input
                            type="text"
                            className="form-control"
                            name="username"
                            value={form.username}
                            onChange={handleChange}
                            placeholder="Ingrese su usuario"
                            required
                        />
                    </div>
                    <div className="mb-3">
                        <label htmlFor="password" className="form-label">
                            Contraseña
                        </label>
                        <input
                            type="password"
                            className="form-control"
                            name="password"
                            value={form.password}
                            onChange={handleChange}
                            placeholder="Ingrese su contraseña"
                            required
                        />
                    </div>
                    <div className="mb-4">
                        <label htmlFor="role" className="form-label">
                            ¿Ud. es ... ?
                        </label>
                        <select
                            className="form-select"
                            name="role"
                            value={form.role}
                            onChange={handleChange}
                        >
                            <option value="Paciente">Paciente</option>
                            <option value="Médico">Médico</option>
                            <option value="Administrativo">
                                Administrativo
                            </option>
                        </select>
                    </div>
                    <button type="submit" className="btn btn-primary w-100">
                        Entrar
                    </button>
                </form>
                {error && (
                    <p className="text-danger mt-3 text-center">{error}</p>
                )}
            </div>
        </div>
    );
}

export default LoginForm;
